package edu.cmu.scs.fluorite.dialogs;

import org.eclipse.jface.dialogs.Dialog;
import org.eclipse.jface.preference.IPreferenceStore;
import org.eclipse.jface.text.BadLocationException;
import org.eclipse.jface.text.IFindReplaceTargetExtension;
import org.eclipse.jface.text.IFindReplaceTargetExtension3;
import org.eclipse.jface.text.ITextViewer;
import org.eclipse.jface.text.ITextViewerExtension6;
import org.eclipse.jface.text.IUndoManager;
import org.eclipse.jface.text.Region;
import org.eclipse.swt.SWT;
import org.eclipse.swt.events.KeyAdapter;
import org.eclipse.swt.events.KeyEvent;
import org.eclipse.swt.events.SelectionAdapter;
import org.eclipse.swt.events.SelectionEvent;
import org.eclipse.swt.graphics.Point;
import org.eclipse.swt.layout.GridData;
import org.eclipse.swt.layout.GridLayout;
import org.eclipse.swt.widgets.Button;
import org.eclipse.swt.widgets.Composite;
import org.eclipse.swt.widgets.Control;
import org.eclipse.swt.widgets.Display;
import org.eclipse.swt.widgets.Group;
import org.eclipse.swt.widgets.Label;
import org.eclipse.swt.widgets.MessageBox;
import org.eclipse.swt.widgets.Shell;
import org.eclipse.swt.widgets.Text;

import edu.cmu.scs.fluorite.commands.FindCommand;
import edu.cmu.scs.fluorite.model.EventRecorder;
import edu.cmu.scs.fluorite.plugin.Activator;
import edu.cmu.scs.fluorite.preferences.Initializer;

public class FindConfigureDialog extends Dialog {
	private Text mSearchText;
	private Text mReplaceText;
	private Button mCaseSensitive;
	private Button mForward;
	private Button mBackward;
	private Button mRegExpMode;
	private Button mMatchWord;
	private Button mWrap;
	private Button mIncremental;
	private Button mAll;
	private Button mSelectedLines;

	private Button mReplaceAndFindButton;
	private Button mReplaceButton;

	private String mInitialSearchString;

	private boolean mCreatedDialogArea;

	private IFindReplaceTargetExtension mFindReplaceTargetExt;
	private IFindReplaceTargetExtension3 mFindReplaceTargetExt3;
	private ITextViewer mViewer;
	private Point mInitialSelection;
	private Point mLastSelection;

	private static final int FIND = 0;
	private static final int REPLACE_AND_FIND = 1;
	private static final int REPLACE = 2;
	private static final int REPLACE_ALL = 3;
	private static final int CLOSE = 4;

	private static FindConfigureDialog _instance = null;

	public static FindConfigureDialog getInstance() {
		return _instance;
	}

	public FindConfigureDialog(Shell shell, String initialSearchString,
			ITextViewer viewer) {
		super(shell);
		setShellStyle(SWT.CLOSE | SWT.MODELESS | SWT.BORDER | SWT.TITLE
				| SWT.RESIZE);
		mInitialSearchString = initialSearchString;
		mCreatedDialogArea = false;
		mViewer = viewer;
		mFindReplaceTargetExt = (IFindReplaceTargetExtension) viewer
				.getFindReplaceTarget();
		mFindReplaceTargetExt3 = (IFindReplaceTargetExtension3) viewer
				.getFindReplaceTarget();
		mInitialSelection = viewer.getSelectedRange();
		mLastSelection = new Point(0, 0);
	}

	@Override
	protected Control createDialogArea(Composite parent) {
		getShell().setText("Find/Replace");
		Composite comp = new Composite(parent, SWT.None);
		comp.setLayout(new GridLayout());
		comp.setLayoutData(new GridData(GridData.FILL_BOTH));

		Composite stringComp = new Composite(comp, SWT.NONE);
		stringComp.setLayout(new GridLayout(2, false));
		stringComp.setLayoutData(new GridData(GridData.FILL_HORIZONTAL));

		Label label = new Label(stringComp, SWT.NONE);
		label.setText("&Find:");

		mSearchText = new Text(stringComp, SWT.BORDER);
		mSearchText.setLayoutData(new GridData(SWT.FILL, SWT.CENTER, true,
				false));

		label = new Label(stringComp, SWT.NONE);
		label.setText("R&eplace with:");

		mReplaceText = new Text(stringComp, SWT.BORDER);
		mReplaceText.setLayoutData(new GridData(SWT.FILL, SWT.CENTER, true,
				false));

		stringComp.pack();

		Composite dirScopeComp = new Composite(comp, SWT.NONE);
		dirScopeComp.setLayout(new GridLayout(2, true));
		dirScopeComp.setLayoutData(new GridData(GridData.FILL_HORIZONTAL));

		Group dirGroup = new Group(dirScopeComp, SWT.NONE);
		dirGroup.setText("Direction");
		dirGroup.setLayout(new GridLayout());
		dirGroup.setLayoutData(new GridData(GridData.FILL_BOTH));

		mForward = new Button(dirGroup, SWT.RADIO);
		mForward.setText("F&orward");
		mBackward = new Button(dirGroup, SWT.RADIO);
		mBackward.setText("&Backward");

		Group scopeGroup = new Group(dirScopeComp, SWT.NONE);
		scopeGroup.setText("Scope");
		scopeGroup.setLayout(new GridLayout());
		scopeGroup.setLayoutData(new GridData(GridData.FILL_HORIZONTAL));

		mAll = new Button(scopeGroup, SWT.RADIO);
		mAll.setText("A&ll");
		mAll.addSelectionListener(new SelectionAdapter() {

			@Override
			public void widgetSelected(SelectionEvent e) {
				mFindReplaceTargetExt.setScope(null);
				mViewer.setSelectedRange(mInitialSelection.x,
						mInitialSelection.y);
			}

		});
		mSelectedLines = new Button(scopeGroup, SWT.RADIO);
		mSelectedLines.setText("Selec&ted lines");
		mSelectedLines.addSelectionListener(new SelectionAdapter() {

			@Override
			public void widgetSelected(SelectionEvent e) {
				Point lineInfo = mFindReplaceTargetExt.getLineSelection();
				mFindReplaceTargetExt.setScope(new Region(lineInfo.x,
						lineInfo.y));
				mViewer.setSelectedRange(lineInfo.x, 0);
			}

		});

		Group optionsGroup = new Group(dirScopeComp, SWT.NONE);
		optionsGroup.setText("Options");
		optionsGroup.setLayout(new GridLayout(2, false));
		optionsGroup.setLayoutData(new GridData(SWT.FILL, SWT.TOP, true, false,
				2, 1));

		mCaseSensitive = new Button(optionsGroup, SWT.CHECK);
		mCaseSensitive.setText("&Case sensitive");

		mWrap = new Button(optionsGroup, SWT.CHECK);
		mWrap.setText("Wra&p search");
		mWrap.setLayoutData(new GridData(GridData.FILL_HORIZONTAL));

		mMatchWord = new Button(optionsGroup, SWT.CHECK);
		mMatchWord.setText("&Whole word");

		mIncremental = new Button(optionsGroup, SWT.CHECK);
		mIncremental.setText("&Incremental");
		mIncremental.setLayoutData(new GridData(GridData.FILL_HORIZONTAL));

		mRegExpMode = new Button(optionsGroup, SWT.CHECK);
		mRegExpMode.setText("Regular e&xpressions");
		mRegExpMode.setLayoutData(new GridData(SWT.FILL, SWT.TOP, true, false,
				2, 1));

		dirScopeComp.pack();

		Composite buttonsComp = new Composite(comp, SWT.NONE);
		GridLayout buttonsLayout = new GridLayout();
		buttonsComp.setLayout(buttonsLayout);

		createButton(buttonsComp, FIND, "Fi&nd", true);
		mReplaceAndFindButton = createButton(buttonsComp, REPLACE_AND_FIND,
				"Replace/Fin&d", false);

		mReplaceButton = createButton(buttonsComp, REPLACE, "&Replace", false);
		createButton(buttonsComp, REPLACE_ALL, "Replace &All", false);

		setReplaceButtonsEnabled(false);

		buttonsComp.setLayoutData(new GridData(SWT.RIGHT, SWT.BOTTOM, true,
				true));
		buttonsLayout.numColumns = 2;

		// populate fields
		if (mInitialSearchString != null) {
			mSearchText.setText(mInitialSearchString);
		}

		IPreferenceStore prefStore = Activator.getDefault()
				.getPreferenceStore();

		if (prefStore.getBoolean(Initializer.Pref_FindForward)) {
			mForward.setSelection(true);
		} else {
			mBackward.setSelection(true);
		}

		mCaseSensitive.setSelection(prefStore
				.getBoolean(Initializer.Pref_FindCaseSensitive));
		mWrap.setSelection(prefStore
				.getBoolean(Initializer.Pref_FindWrapSearch));
		mMatchWord.setSelection(prefStore
				.getBoolean(Initializer.Pref_FindWholeWord));

		// TODO: implement incremental find later.
		mIncremental.setEnabled(false);

		mRegExpMode.setSelection(prefStore
				.getBoolean(Initializer.Pref_FindRegExp));

		mSearchText.selectAll();

		mFindReplaceTargetExt.beginSession();

		// set the scope
		Point selectedRange = mViewer.getSelectedRange();
		try {
			if (mViewer.getDocument().getLineOfOffset(selectedRange.x) != mViewer
					.getDocument().getLineOfOffset(
							selectedRange.x + selectedRange.y)) {
				Point lineInfo = mFindReplaceTargetExt.getLineSelection();
				mFindReplaceTargetExt.setScope(new Region(lineInfo.x,
						lineInfo.y));
				mViewer.setSelectedRange(lineInfo.x, 0);

				mSelectedLines.setSelection(true);
			} else {
				mAll.setSelection(true);
			}
		} catch (BadLocationException e) {
			e.printStackTrace();
		}

		// when the search string is changed, the replace buttons should be
		// disabled.
		mSearchText.addKeyListener(new KeyAdapter() {

			@Override
			public void keyPressed(KeyEvent e) {
				setReplaceButtonsEnabled(false);

				super.keyPressed(e);
			}

		});

		mCreatedDialogArea = true;

		_instance = this;

		return comp;
	}

	private void setReplaceButtonsEnabled(boolean enabled) {
		mReplaceAndFindButton.setEnabled(enabled);
		mReplaceButton.setEnabled(enabled);
	}

	@Override
	protected void buttonPressed(int buttonId) {

		switch (buttonId) {
		case FIND:
			find();
			break;

		case REPLACE_AND_FIND:
			replaceAndFind();
			break;

		case REPLACE:
			replace();
			break;

		case REPLACE_ALL:
			replaceAll();
			break;

		case CLOSE:
			cancelPressed();
			break;
		}
	}

	private void replaceAll() {
		int offset = getOffset();
		int count = 0;

		IUndoManager undoManager = null;
		if (mViewer instanceof ITextViewerExtension6) {
			undoManager = ((ITextViewerExtension6) mViewer).getUndoManager();
		}

		if (undoManager != null) {
			undoManager.beginCompoundChange();
		}

		while (performFind(offset) != -1) {
			performReplace();
			offset = mLastSelection.x + mReplaceText.getText().length();

			++count;
		}

		if (undoManager != null) {
			undoManager.endCompoundChange();
		}

		MessageBox msgBox = new MessageBox(getShell(), SWT.OK);
		msgBox.setText("Replace All");
		msgBox.setMessage("" + count + " matches replaced");
		msgBox.open();

		FindCommand command = new FindCommand();
		command.setSelection("Replace All");
		fillCommandParameters(command);
		EventRecorder.getInstance().recordCommand(command);
	}

	private void replaceAndFind() {
		performReplace();
		int offset = getOffset();
		performFind(offset);

		FindCommand command = new FindCommand();
		command.setSelection("Replace/Find");
		command.setOffset(offset);
		fillCommandParameters(command);
		EventRecorder.getInstance().recordCommand(command);
	}

	private void replace() {
		performReplace();

		FindCommand command = new FindCommand();
		command.setSelection("Replace");
		fillCommandParameters(command);
		EventRecorder.getInstance().recordCommand(command);
	}

	private void performReplace() {
		mFindReplaceTargetExt3.replaceSelection(mReplaceText.getText(),
				mRegExpMode.getSelection());
		setReplaceButtonsEnabled(false);
	}

	private void find() {
		int offset = getOffset();
		performFind(offset);

		FindCommand command = new FindCommand();
		command.setSelection("Find");
		command.setOffset(offset);
		fillCommandParameters(command);
		EventRecorder.getInstance().recordCommand(command);
	}

	private int performFind(int offset) {
		int ret = mFindReplaceTargetExt3.findAndSelect(offset,
				mSearchText.getText(), mForward.getSelection(),
				mCaseSensitive.getSelection(), mMatchWord.getSelection(),
				mRegExpMode.getSelection());
		if (ret == -1 && mWrap.getSelection()) {
			ret = mFindReplaceTargetExt3.findAndSelect(0,
					mSearchText.getText(), mForward.getSelection(),
					mCaseSensitive.getSelection(), mMatchWord.getSelection(),
					mRegExpMode.getSelection());
		}

		if (ret != -1) {
			mLastSelection.x = ret;
			mLastSelection.y = mSearchText.getText().length();

			setReplaceButtonsEnabled(true);
		} else {
			Display.getCurrent().beep();
		}

		return ret;
	}

	private void fillCommandParameters(FindCommand command) {
		command.setSearchString(mSearchText.getText());
		command.setReplaceString(mReplaceText.getText());
		command.setSearchForward(mForward.getSelection());
		command.setScopeIsSelection(mSelectedLines.getSelection());
		command.setCaseSensitive(mCaseSensitive.getSelection());
		command.setWrapSearch(mWrap.getSelection());
		command.setMatchWholeWord(mMatchWord.getSelection());
		command.setRegExpMode(mRegExpMode.getSelection());
	}

	@Override
	protected void createButtonsForButtonBar(Composite parent) {
		if (!mCreatedDialogArea) {
			return;
		}

		createButton(parent, CLOSE, "Close", false);
	}

	private int getOffset() {
		return mViewer.getSelectedRange().x + mViewer.getSelectedRange().y;
	}

	@Override
	public boolean close() {
		mFindReplaceTargetExt.endSession();
		_instance = null;

		IPreferenceStore prefStore = Activator.getDefault()
				.getPreferenceStore();
		if (prefStore != null) {
			prefStore.setValue(Initializer.Pref_FindForward,
					mForward.getSelection());
			prefStore.setValue(Initializer.Pref_FindCaseSensitive,
					mCaseSensitive.getSelection());
			prefStore.setValue(Initializer.Pref_FindWrapSearch,
					mWrap.getSelection());
			prefStore.setValue(Initializer.Pref_FindWholeWord,
					mMatchWord.getSelection());
			prefStore.setValue(Initializer.Pref_FindRegExp,
					mRegExpMode.getSelection());
		}

		return super.close();
	}

}

package edu.cmu.scs.fluorite.commands;

import java.util.HashMap;
import java.util.Map;

import org.eclipse.jface.text.IFindReplaceTarget;
import org.eclipse.jface.text.IFindReplaceTargetExtension;
import org.eclipse.jface.text.IFindReplaceTargetExtension3;
import org.eclipse.jface.text.Region;
import org.eclipse.swt.custom.StyledText;
import org.eclipse.swt.dnd.Clipboard;
import org.eclipse.swt.dnd.TextTransfer;
import org.eclipse.swt.graphics.Point;
import org.eclipse.swt.widgets.Shell;
import org.eclipse.ui.IEditorPart;
import org.w3c.dom.Attr;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;

import edu.cmu.scs.fluorite.dialogs.FindConfigureDialog;
import edu.cmu.scs.fluorite.model.EventRecorder;
import edu.cmu.scs.fluorite.util.Utilities;

public class FindCommand extends AbstractCommand {

	public FindCommand() {
		mSearchString = null;
		mSearchForward = true;
		mReplaceAll = false;
		mCaseSensitive = false;
		mMatchWholeWord = false;
		mRegExpMode = false;
		mReplaceString = null;
		mWrapSearch = false;
		mScopeIsSelection = false;
		// mFindAfterReplace=false;
		mOkPressed = false;
	}

	public FindCommand(String findString) {
		this();
		mSearchString = findString;
	}

	private boolean mReplaceAll;
	private boolean mScopeIsSelection;
	// private boolean mFindAfterReplace;
	private boolean mWrapSearch;
	private boolean mSearchForward;
	private boolean mCaseSensitive;
	private boolean mMatchWholeWord;
	private boolean mRegExpMode;
	private String mSearchString;
	private String mReplaceString;
	private boolean mOkPressed;
	private int mOffset;
	private String mSelection;

	public static final String XML_Selection_Attr = "selection";
	public static final String XML_Offset_Attr = "offset";
	public static final String XML_ReplaceAll_Attr = "replaceAll";
	public static final String XML_SelectionScope_Attr = "selectionScope";
	// public static final String XML_FindAfterReplace_Attr="findAfterReplace";
	public static final String XML_WrapSearch_Attr = "wrapSearch";
	public static final String XML_Forward_Attr = "forward";
	public static final String XML_CaseSensitive_Attr = "caseSensitive";
	public static final String XML_MatchWord_Attr = "matchWord";
	public static final String XML_RegExp_Attr = "regexp";
	public static final String XML_SearchString_Tag = "searchString";
	public static final String XML_ReplaceString_Tag = "replaceString";

	private void configureWithSearchTerm(Shell shell, String initialSearchString) {
		FindConfigureDialog dlg = new FindConfigureDialog(shell,
				initialSearchString, Utilities.getSourceViewer(EventRecorder
						.getInstance().getEditor()));
		dlg.open();
	}

	public void configureNew(Shell shell) {
		if (FindConfigureDialog.getInstance() != null) {
			// Do nothing.
			return;
		}

		String initialSearchString = null;
		IEditorPart editor = Utilities.getActiveEditor();
		if (editor != null) {
			StyledText widget = Utilities.getStyledText(editor);
			if (widget != null) {
				initialSearchString = widget.getSelectionText();
				if (initialSearchString.length() == 0)
					initialSearchString = null;
			}
		}
		configureWithSearchTerm(shell, initialSearchString);
	}

	public void dump() {
		System.out.println(getDescription());
	}

	public boolean execute(IEditorPart target) {
		IFindReplaceTarget findTarget = Utilities.getFindReplaceTarget(target);
		if (findTarget != null) {
			if (findTarget instanceof IFindReplaceTargetExtension) {
				IFindReplaceTargetExtension findTarget1 = (IFindReplaceTargetExtension) findTarget;
				findTarget1.beginSession();
				try {
					StyledText widget = Utilities.getStyledText(target);
					if (findTarget instanceof IFindReplaceTargetExtension3) {
						IFindReplaceTargetExtension3 findTarget3 = (IFindReplaceTargetExtension3) findTarget;

						int boundaryStart = 0;
						int boundaryEnd = widget.getCharCount() - 1;
						if (mScopeIsSelection) {
							Point sel = widget.getSelectionRange();
							boundaryStart = sel.x;
							boundaryEnd = sel.x + sel.y;
							findTarget1.setScope(new Region(sel.x, sel.y));
						} else {
							findTarget1.setScope(null);
						}

						// if we have a replace string
						if (mReplaceString != null && mReplaceAll) {
							// by convention, replaceall always returns true
							doReplaceAll(findTarget3, widget);
							return true;
						}

						String searchString = mSearchString;
						if (searchString == null) {
							// try getting search text from clipboard, if not
							// clipboard text, then use current selection
							try {
								Clipboard cb = new Clipboard(
										widget.getDisplay());
								TextTransfer transfer = TextTransfer
										.getInstance();
								searchString = (String) cb
										.getContents(transfer);
							} catch (IllegalArgumentException e) {
								// no text on clipboard; don't print error
							} catch (Exception e) {
								e.printStackTrace();
							}

							// backup strategy is to use the selection
							if (searchString == null)
								searchString = widget.getSelectionText();
						}
						if (searchString == null || searchString.length() == 0)
							return false;

						int startPos = widget.getCaretOffset() - 1;
						if (mScopeIsSelection) {
							if (mSearchForward)
								startPos = boundaryStart;
							else
								startPos = boundaryEnd;
						}
						int findPos = ((IFindReplaceTargetExtension3) findTarget)
								.findAndSelect(startPos, searchString,
										mSearchForward, mCaseSensitive,
										mMatchWholeWord, mRegExpMode);
						if (!isInRange(searchString, boundaryStart,
								boundaryEnd, findPos) && mWrapSearch) {
							if (!mScopeIsSelection) {
								if (mSearchForward)
									startPos = boundaryStart;
								else
									startPos = boundaryEnd;

								findPos = ((IFindReplaceTargetExtension3) findTarget)
										.findAndSelect(startPos, searchString,
												mSearchForward, mCaseSensitive,
												mMatchWholeWord, mRegExpMode);
							}
						}

						boolean inRange = (isInRange(searchString,
								boundaryStart, boundaryEnd, findPos));
						if (inRange && mReplaceString != null) {
							findTarget3.replaceSelection(mReplaceString,
									mRegExpMode);
						}
						return inRange;
					}
				} finally {
					((IFindReplaceTargetExtension) findTarget).endSession();
				}
			}
		}

		return false;
	}

	private boolean isInRange(String searchString, int boundaryStart,
			int boundaryEnd, int foundPos) {
		if (foundPos >= boundaryStart
				&& foundPos + searchString.length() <= boundaryEnd)
			return true;
		return false;
	}

	private void doReplaceAll(IFindReplaceTargetExtension3 findTarget,
			StyledText widget) {
		int docPos = 0;
		int endBoundary = 0;
		if (mScopeIsSelection) {
			docPos = widget.getSelection().x;
			endBoundary = widget.getSelection().y;
		}

		while (true) {
			// perform a find
			int foundPos = findTarget.findAndSelect(docPos, mSearchString,
					mSearchForward, mCaseSensitive, mMatchWholeWord,
					mRegExpMode);
			if (foundPos < 0 || (mScopeIsSelection && foundPos >= endBoundary))
				break;

			// replace the text
			int oldDocLength = widget.getCharCount();
			int searchStringSize = widget.getSelectionCount(); // accounts for
																// regular
																// expressions
			findTarget.replaceSelection(mReplaceString, mRegExpMode);
			int newDocLength = widget.getCharCount();
			int amtAdded = newDocLength - oldDocLength;
			docPos = foundPos + searchStringSize + amtAdded;
			endBoundary += amtAdded;

		}
	}

	public String getCategory() {
		return EventRecorder.MacroCommandCategory;
	}

	public String getCategoryID() {
		return EventRecorder.MacroCommandCategoryID;
	}

	public String getDescription() {
		StringBuffer buffer = new StringBuffer();
		buffer.append("Perform Find with the following parameters:\n");
		buffer.append("Search String: " + mSearchString + "\n");
		buffer.append("Case Sensitive: " + mCaseSensitive + "\n");
		buffer.append("Search Forward: " + mSearchForward + "\n");
		buffer.append("Match whole word: " + mMatchWholeWord + "\n");
		buffer.append("Selection scope: " + mScopeIsSelection + "\n");
		buffer.append("Wrap Search: " + mWrapSearch + "\n");
		// buffer.append("FindAfterReplace: "+mFindAfterReplace+"\n");
		buffer.append("Reg Exp Mode: " + mRegExpMode + "\n");
		buffer.append("Replace All: " + mReplaceAll + "\n");
		buffer.append("Replace String: " + mReplaceString + "\n");
		return buffer.toString();
	}

	public String getName() {
		return "Find "
				+ ((mSearchString != null) ? mSearchString
						: "<clipboard/selection>");
	}

	public Map<String, String> getAttributesMap() {
		Map<String, String> attrMap = new HashMap<String, String>();
		attrMap.put(XML_Selection_Attr, mSelection);
		attrMap.put(XML_Offset_Attr, Integer.toString(mOffset));
		attrMap.put(XML_Forward_Attr, Boolean.toString(mSearchForward));
		attrMap.put(XML_CaseSensitive_Attr, Boolean.toString(mCaseSensitive));
		attrMap.put(XML_RegExp_Attr, Boolean.toString(mRegExpMode));
		attrMap.put(XML_MatchWord_Attr, Boolean.toString(mMatchWholeWord));
		attrMap.put(XML_ReplaceAll_Attr, Boolean.toString(mReplaceAll));
		// attrMap.put(XML_FindAfterReplace_Attr,
		// Boolean.toString(mFindAfterReplace));
		attrMap.put(XML_SelectionScope_Attr,
				Boolean.toString(mScopeIsSelection));
		attrMap.put(XML_WrapSearch_Attr, Boolean.toString(mWrapSearch));
		return attrMap;
	}

	public Map<String, String> getDataMap() {
		Map<String, String> dataMap = new HashMap<String, String>();
		if (mSearchString != null)
			dataMap.put(XML_SearchString_Tag, mSearchString);
		if (mReplaceString != null)
			dataMap.put(XML_ReplaceString_Tag, mReplaceString);

		return dataMap;
	}

	@Override
	public void createFrom(Element commandElement) {
		super.createFrom(commandElement);
		
		Attr attr = null;
		String value = null;
		NodeList nodeList = null;
		
		if ((attr = commandElement.getAttributeNode(XML_Selection_Attr)) != null) {
			value = attr.getValue();
			mSelection = value.equals("null") ? null : value;
		}
		else {
			mSelection = null;
		}
		
		if ((attr = commandElement.getAttributeNode(XML_Offset_Attr)) != null) {
			mOffset = Integer.parseInt(attr.getValue());
		}
		
		if ((attr = commandElement.getAttributeNode(XML_Forward_Attr)) != null) {
			mSearchForward = Boolean.parseBoolean(attr.getValue());
		}
		
		if ((attr = commandElement.getAttributeNode(XML_CaseSensitive_Attr)) != null) {
			mCaseSensitive = Boolean.parseBoolean(attr.getValue());
		}
		
		if ((attr = commandElement.getAttributeNode(XML_RegExp_Attr)) != null) {
			mRegExpMode = Boolean.parseBoolean(attr.getValue());
		}
		
		if ((attr = commandElement.getAttributeNode(XML_MatchWord_Attr)) != null) {
			mMatchWholeWord = Boolean.parseBoolean(attr.getValue());
		}
		
		if ((attr = commandElement.getAttributeNode(XML_ReplaceAll_Attr)) != null) {
			mReplaceAll = Boolean.parseBoolean(attr.getValue());
		}
		
		if ((attr = commandElement.getAttributeNode(XML_SelectionScope_Attr)) != null) {
			mScopeIsSelection = Boolean.parseBoolean(attr.getValue());
		}
		
		if ((attr = commandElement.getAttributeNode(XML_WrapSearch_Attr)) != null) {
			mWrapSearch = Boolean.parseBoolean(attr.getValue());
		}
		
		if ((nodeList = commandElement.getElementsByTagName(XML_SearchString_Tag)).getLength() > 0) {
			Node textNode = nodeList.item(0);
			value = textNode.getTextContent();
			mSearchString = value.equals("null") ? null : value;
		}
		else {
			mSearchString = null;
		}
		
		if ((nodeList = commandElement.getElementsByTagName(XML_ReplaceString_Tag)).getLength() > 0) {
			Node textNode = nodeList.item(0);
			value = textNode.getTextContent();
			mReplaceString = value.equals("null") ? null : value;
		}
		else {
			mReplaceString = null;
		}
	}

	public String getCommandType() {
		return "FindCommand";
	}

	public void setSearchForward(boolean forward) {
		mSearchForward = forward;
	}

	public boolean isReplaceAll() {
		return mReplaceAll;
	}

	public void setReplaceAll(boolean replaceAll) {
		mReplaceAll = replaceAll;
	}

	public boolean isCaseSensitive() {
		return mCaseSensitive;
	}

